<template>
  <section id="RecommendedCategories" :class="{ whiteRecommendTheme: !isDarkTheme }">
    <p class="recomendation_loading" v-for="loadingCategory of loadingCategories" :key="loadingCategory.id" v-show="!isLoaded"></p>
    <router-link :to="category.link" class="recomendation" v-for="category of categories.slice(0, 10)" :key="category.id" v-show="isLoaded">
      {{ category.title }}
    </router-link>
    <button>  
      <svg width="19" height="18" viewBox="0 0 19 18" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <rect x="19" width="18" height="19" transform="rotate(90 19 0)" fill="url(#pattern0)"/>
        <defs>
          <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
            <use xlink:href="#image0_1" transform="matrix(0.0109954 0 0 0.0104167 -0.0277778 0)"/>
          </pattern>
          <image id="image0_1" width="96" height="96" xlink:href="data:image/png;base64,"/>
        </defs>
      </svg>
    </button>
  </section>
</template>

<script lang="ts">  
  import { defineComponent } from 'vue';
  import { ref, watch } from 'vue';
  import store from '@/store/index';
  import axios from 'axios';

  interface Category {
    id: number,
    title: string,
    link: string,
  }

  export default defineComponent({
    name: 'RecommendedCategoriesComp',
    data() {
      return {
        
      }
    },
    setup() {
      const isDarkTheme = ref(store.state.isDarkTheme);
      const isLoaded = ref(false);
      const categories = ref([] as Category[]);
      const loadingCategories = ref([
          {
            id: 0
          },
          {
            id: 1
          },
          {
            id: 2
          },
          {
            id: 3
          },
          {
            id: 4
          },
          {
            id: 5
          },
          {
            id: 6
          },
        ]);

      watch(() => store.state.isDarkTheme, () => {
        isDarkTheme.value = store.state.isDarkTheme;
      });

      return {
        isDarkTheme,
        isLoaded,
        categories,
        loadingCategories
      }
    },
    methods: {
      async getRecommendedCategories() {
        const url = new URL('http://79.174.12.75:80/api/article/category/popular/');

        let result;
        axios.get(url.toString())
          .then((res) => {
            result = res;
          })
          .catch((e) => {
            this.$router.push('/techWorks');
          })

        this.categories = Object.values(result.data);

        if(this.categories.length) {
          this.isLoaded = true;
        }
      }
    },
    mounted() {
      this.getRecommendedCategories();
    }
  })
</script>

<style lang="scss" scoped>
  @import "@/styles/_variables.scss";

  section {
    background-color: $DarkBgBlocksTheme;
  }

  #RecommendedCategories {
    display: flex;
    justify-content: space-around;
    align-items: center;
    margin-left: 2.25%;
    width: 97.5%;
    height: 30px;
    border: 2px solid $BorderColor;
    border-radius: 5px;
    transition: 400ms ease;
    .recomendation {
      color: #747474;
      font-size: 16px;
      font-family: $SpaceGrotesk;
      text-decoration: none;
      transition: 400ms ease;
      outline: none;
      cursor: pointer;
    }
    .recomendation:hover {
      color: $HoverColor;
    }
    .recomendation_loading {
      width: 90px;
      height: 10px;
      background-color: $LoadingColor;
      border-radius: 2.5px;
    }
    button {
      margin-left: -12px;
      width: 16px;
      height: 18px;
      background-color: transparent;
      border: 0;
      outline: none;
      cursor: pointer;
    }
  }

  .whiteRecommendTheme {
    background-color: $WhiteBgBlocksTheme;
  }
</style>